
async function deployContract(byteCode, abi, constructorArgs) {
  if (typeof web3 !== 'undefined') {
    web3 = new Web3(web3.currentProvider);
    var curAccount = await ethereum.enable();
    var contractInstance = new web3.eth.Contract(abi);
    contractInstance.deploy({
        data: '0x' + byteCode
    })
    .send({
        from: curAccount.toString(),
    })
    .on('error', function(error){
      // Tell the user there was an error during deployment
    })
    .then(function(newContractInstance){
        console.log(newContractInstance.options.address) // instance with the new contract address
    });
  }
  else {
    // Tell user web3 provider was not recognized
  }
}

